import React, { Component } from 'react'
import fire from './Firebase'
import FunComp from './FunComp'

var dup = [{}]
var temp = [{}]
export class GetDataFromForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
      flight: [],
      showMore: {},
    }
  }

  componentDidMount() {
    console.log('ComponentDidMount method called')
    var ref = fire.database().ref().child('FlightsData')
    ref.on('value', (snap) => {
      dup = snap.val()
      console.log(dup)
    })
  }

  handleSubmit = (e) => {
    if (fire.auth().currentUser != null) {
      console.log('user' + fire.auth().currentUser.uid)
    }
    e.preventDefault()

    for (var i = 0; i < dup.length; i++) {
      if (
        dup[i].source == this.props.source &&
        dup[i].destination == this.props.destination &&
        dup[i].deptDate == this.props.departureDate
      ) {
        console.log('baroda')
        temp[i] = dup[i]
      } else {
        console.log('not working')
      }
    }

    this.setState({
      flight: temp,
    })
    temp = [{}]
    console.log('state value' + this.state.flight[0])

    // refernce.push(data)
    // fire
    //   .database()
    //   .ref('search')
    //   .ref('destination')
    //   .push(this.props.destination)
    // fire.database().ref('search'). .push('hello dhaval')
  }

  // handleInfo = (value) => (event) => {
  //   event.preventDefault()
  //   {
  //     alert(value.seats)
  //   }
  //   return <h1>{value.seats}</h1>
  // }

  handleInfo(key) {
    //alert('value:' + para.seats)
    this.setState((prevState) => ({
      showMore: {
        [key]: !(
          Object.keys(prevState.showMore).length > 0 && prevState.showMore[key]
        ),
      },
    }))

    // let ref = fire.database().ref('history')
    // ref.child(fire.auth().currentUser.uid).push(key)
  }

  // info = (value) => {
  //   return this.state.showMore ? <div>{value.seats}</div> : null
  // }

  renderMap = () => {
    return Object.entries(this.state.flight).map(([key, value]) => (
      <div key={key}>
        <div>{value.source}</div>
        <div>{value.destination}</div>
        <div>{value.deptDate}</div>
        <button
          hidden={value.seats == null ? true : false}
          onClick={(e) => {
            e.preventDefault()
            this.handleInfo(key)
          }}
        >
          More info
        </button>
        <a href={value.link} target='blank'>
          Book
        </a>
        {Object.keys(this.state.showMore).length > 0 &&
          this.state.showMore[key] && <div id={`id-${key}`}>{value.seats}</div>}
      </div>
    ))
  }

  render() {
    return (
      <div>
        <button onClick={this.handleSubmit}>Submit Data</button>
        {this.renderMap()}
      </div>
    )
  }
}

export default GetDataFromForm
