var dup = [{}]
var temp = [{}]
export class GetDataFromForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
      flight: [
        // {},
        // {
        //   carrier: 'air France',
        //   aircraft: 'Boeing 777',
        //   depDate: 'dfsdss',
        //   arrDate: 'czczxcz',
        // },
      ],
      // hello: [1, 2, 3, 4],
    }
  }

  componentDidMount() {
    var ref = fire.database().ref().child('FlightsData')
    // const querybase = require('querybase')
    // const query = querybase.ref(ref, ['source', 'destination'])
    // var queryComplete = query.where({
    //   source: 'vadodara',
    //   destination: 'delhi',
    // })
    // var count = -1
    ref.on('value', (snap) => {
      dup = snap.val()
      console.log(dup)
      // this.setState({
      //   flight: snap.val(),
      // })
    })
    console.log('object: ' + this.state.flight[0])

    // ref.once('value', (snap) => {
    //   // console.log(snap.val())
    //   // console.log('key: ' + snap.key)
    //   snap.forEach((dhaval) => {
    //     var data = dhaval.val()
    //     console.log('data: ' + data)
    //   })
    // })
    // for (var i = 0; i < dup.length; i = i + 1) {
    //   console.log('tata')
    //   if (dup[0]['source'] == 'vadodara') {
    //     // this.setState({
    //     //   flight: dup,
    //     // })
    //     // alert(dup[i])
    //     temp[0] = dup[0]
    //   } else {
    //     // alert('tirth : ' + i)
    //   }
    // }

    console.log('temp : ' + this.state.flight[0])
    for (var i = 0; i < dup.length; i++) {
      if (dup[i].source == 'vadodara' && dup[i].destination == 'bombay') {
        console.log('baroda')
        this.setState({
          flight: [...this.state.flight, dup],
        })
      } else {
        console.log('not working')
      }
    }
    console.log('value of flight: ' + this.state.flight)
  }

  handleSubmit = (event) => {
    event.preventDefault()
    let refernce = fire.database().ref('Search')
    const data = {
      source: this.props.source,
      destination: this.props.destination,
      departureDate: this.props.departureDate,
      string: 'dhaval agrawal',
    }

    refernce.push(data)
    // fire
    //   .database()
    //   .ref('search')
    //   .ref('destination')
    //   .push(this.props.destination)
    // fire.database().ref('search'). .push('hello dhaval')
  }

  render() {
    return (
      <div>
        <button onClick={this.handleSubmit}>Submit Data</button>
        {/* //if dup.destination == 'delhi' */}
        {/* {this.state.flight.map((num) => (
          <>
            <div>{num.source}</div>
            <div>{num.destination}</div>
            <div>{num.seats}</div>
            <div>{num.deptDate}</div>
          </>
        ))} */}
        {Object.entries(this.state.flight).map(([key, value]) => (
          <div key={key}>
            <div>{value.source}</div>
            <div>{value.destination}</div>
            <div>{value.seats}</div>
            <div>{value.deptDate}</div>
            {console.log('hello' + value)};
          </div>
        ))}
      </div>
    )
  }
}

export default GetDataFromForm
