import React, { Component } from 'react'

export default class Product extends Component {
  constructor(props) {
    super(props)

    this.state = {
      ProductId: '',
      qty: 0,
    }
  }

  addtoCart = (pid) => {
    console.log(pid)
    this.setState({
      ProductId: pid,
      qty: this.state.qty + 1,
    })
  }

  render() {
    return (
      <div>
        <button onClick={() => this.addtoCart(1)}>Add to Cart</button>
        <Cart></Cart>
      </div>
    )
  }
}

class Cart extends Component {
  constructor(props) {
    super(props)
    console.log('within constructor of cart component')
    this.state = {
      qty: 0,
    }
  }

  updateValue = () => {
    this.setState({
      qty: this.state.qty + 1,
    })
  }

  render() {
    return (
      <div>
        <h1>Quantity: {this.state.qty}</h1>
        <button onClick={this.updateValue}>Increase</button>
      </div>
    )
  }
  componentDidMount() {
    console.log('within componentDidMount method')
  }
  componentDidUpdate(prevProps, prevState) {
    console.log('within componentDidUpdate method')
  }

  static getDerivedStateFromProps(props, state) {
    console.log('within getderivedstatefromprops method')
    return null
  }
}
