import React, { Component } from 'react'
import axios from 'axios'
import GetDataFromForm from './GetDataFromForm'

let token = ''
export class Form extends Component {
  constructor(props) {
    super()
    this.state = {
      source: '',
      destination: '',
      date: '',
      journeyType: '',
      adults: 0,
      children: 0,
      infants: 0,
      returnDate: '',
    }
  }

  // componentDidMount() {
  //   const axios = require('axios')
  //   var qs = require('qs')
  //   var data = qs.stringify({
  //     grant_type: 'client_credentials',
  //     client_id: 'egdfiMPA4upZ4DG3BXGuyPxqfFAetEVH',
  //     client_secret: 'bfPfw4TLGRkiF59F',
  //   })
  //   var config = {
  //     method: 'post',
  //     url: 'https://test.api.amadeus.com/v1/security/oauth2/token',
  //     headers: {
  //       'Content-Type': 'application/x-www-form-urlencoded',
  //     },
  //     data: data,
  //   }

  //   axios(config)
  //     .then((response) => {
  //       token = response.data.access_token
  //       console.log(JSON.stringify(response.data))
  //       console.log(token)
  //     })
  //     .catch((error) => {
  //       console.log(error)
  //     })
  // }

  handleSource = (event) => {
    this.setState({
      source: event.target.value,
    })
  }
  handleDestination = (event) => {
    this.setState({
      destination: event.target.value,
    })
  }

  handleChildren = (event) => {
    this.setState({
      children: event.target.value,
    })
  }

  handleDate = (event) => {
    this.setState({
      date: event.target.value,
    })
  }

  handleAdults = (event) => {
    this.setState({
      adults: event.target.value,
    })
  }

  handleInfants = (event) => {
    this.setState({
      infants: event.target.value,
    })
  }

  handleReturnDate = (event) => {
    this.setState({
      returnDate: event.target.value,
    })
  }

  tryDate() {
    if (this.state.journeyType == 'Round Trip') {
      return (
        <input
          type='date'
          name='returnDate'
          value={this.state.returnDate}
          onChange={this.handleReturnDate}
        />
      )
    }
  }

  handleJourney = (event) => {
    this.setState({
      journeyType: event.target.value,
    })
  }

  // handleSubmit = (event) => {
  //   event.preventDefault()
  //   console.log(token)
  //   axios
  //     .get(
  //       `https://test.api.amadeus.com/v2/shopping/flight-offers?originLocationCode=${this.state.source}&destinationLocationCode=${this.state.destination}&departureDate=${this.state.date}&adults=1&travelClass=ECONOMY`,
  //       {
  //         headers: {
  //           Authorization: `Bearer ${token} `,
  //         },
  //       }
  //     )
  //     .then((response) => {
  //       this.populateData(JSON.stringify(response.carriers))
  //       console.log(JSON.stringify(response.data))
  //       console.log('carriers' + JSON.stringify(this.state.flights))
  //     })
  //     .catch((error) => {
  //       console.log('error encountered: ' + error)
  //     })
  // }

  render() {
    const { flights } = this.state
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          journey Type:
          <select value={this.state.journeyType} onChange={this.handleJourney}>
            <option selected value=''></option>
            <option value='One Way'>One Way</option>
            <option value='Round Trip'>Round Trip</option>
            <option value='multicity'>Multi City</option>
          </select>
        </label>
        <div>
          <input
            type='text'
            placeholder='Source'
            name='source'
            value={this.state.source}
            onChange={this.handleSource}
          />
        </div>
        <div>
          <input
            type='text'
            placeholder='Destination'
            name='destination'
            value={this.state.destination}
            onChange={this.handleDestination}
          />
        </div>
        <div>{this.tryDate()}</div>
        <div>
          <input
            type='date'
            placeholder='select Date'
            name='date'
            value={this.state.date}
            onChange={this.handleDate}
          />
        </div>

        <div>
          <input
            type='number'
            placeholder='No. of Adults'
            name='adults'
            value={this.state.adults}
            onChange={this.handleAdults}
          />
        </div>
        <div>
          <input
            type='number'
            placeholder='No. of Children'
            name='children'
            value={this.state.children}
            onChange={this.handleChildren}
          />
        </div>
        <div>
          <input
            type='number'
            placeholder='No. of Infants'
            name='infants'
            value={this.state.infants}
            onChange={this.handleInfants}
          />
        </div>
        <div>
          <GetDataFromForm
            source={this.state.source}
            destination={this.state.destination}
            trip={this.state.journeyType}
            adults={this.state.adults}
            child={this.state.children}
            infant={this.state.infants}
            departureDate={this.state.date}
            returnDate={this.state.returnDate}
          ></GetDataFromForm>
        </div>
      </form>
    )
  }
}

export default Form
