import React, { Component } from 'react'
import fire from './Firebase'
import FunComp from './FunComp'
import TempD from './TempD'

var dup = [{}]
var temp = [{}]
export class GetDataFromForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
      flight: [
        ,// {
      //   carrier: 'air France',
      //   aircraft: 'Boeing 777',
      //   depDate: 'dfsdss',
      //   arrDate: 'czczxcz',
      // },
      ],
      info: false,
    }
  }

  componentDidMount() {
    console.log('ComponentDidMount method called')
    var ref = fire.database().ref().child('FlightsData')
    ref.on('value', (snap) => {
      dup = snap.val()
      console.log(dup)
    })
  }

  handleSubmit = (e) => {
    e.preventDefault()

    for (var i = 0; i < dup.length; i++) {
      if (
        dup[i].source == this.props.source &&
        dup[i].destination == this.props.destination &&
        dup[i].deptDate == this.props.departureDate
      ) {
        console.log('baroda')
        temp[i] = dup[i]
      } else {
        console.log('not working')
      }
    }

    this.setState({
      flight: temp,
    })
    console.log('state value' + this.state.flight.length)

    // this.fetchData()

    // let refernce = fire.database().ref('Search')
    // const data = {
    //   source: this.props.source,
    //   destination: this.props.destination,
    //   departureDate: this.props.departureDate,
    //   string: 'dhaval agrawal',
    // }

    // refernce.push(data)
    // fire
    //   .database()
    //   .ref('search')
    //   .ref('destination')
    //   .push(this.props.destination)
    // fire.database().ref('search'). .push('hello dhaval')
  }

  // passData=(event)=>{
  //   <TempD ></TempD>
  // }

  // handleInfo = (value) => (event) => {
  //   event.preventDefault()
  //   {
  //     alert(value.seats)
  //   }
  //   return <h1>{value.seats}</h1>
  // }

  handleInfo(para) {
    alert('value:' + para.seats)
    this.setState({
      info: true,
    })
  }

  render() {
    return (
      <div>
        <button onClick={this.handleSubmit}>Submit Data</button>
        {Object.entries(this.state.flight).map(([key, value]) => (
          <div key={key}>
            <div>{value.source}</div>
            <div>{value.destination}</div>
            <div>{value.deptDate}</div>
            <button
              hidden={value.seats == null ? true : false}
              onClick={(e) => {
                e.preventDefault()
                this.handleInfo(value)
              }}
            >
              More info
            </button>
          </div>
        ))}
      </div>
    )
  }
}

export default GetDataFromForm
