import React, { Component } from 'react'
import axios from 'axios'

let token = ''
export class GetDataFromForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
      flight: [
        {
          carrier: '',
          aircraft: '',
        },
        // {
        //   carrier: 'air France',
        //   aircraft: 'Boeing 777',
        //   depDate: 'dfsdss',
        //   arrDate: 'czczxcz',
        // },
      ],
      hello: [1, 2, 3, 4],
    }
  }

  componentDidMount() {
    const axios = require('axios')
    var qs = require('qs')
    var data = qs.stringify({
      grant_type: 'client_credentials',
      client_id: 'egdfiMPA4upZ4DG3BXGuyPxqfFAetEVH',
      client_secret: 'bfPfw4TLGRkiF59F',
    })
    var config = {
      method: 'post',
      url: 'https://test.api.amadeus.com/v1/security/oauth2/token',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      data: data,
    }

    axios(config)
      .then((response) => {
        token = response.data.access_token
        console.log('token value:' + token)
        console.log(JSON.stringify(response.data))
      })
      .catch((error) => {
        console.log(error)
      })
  }

  handleSubmit = (event) => {
    event.preventDefault()
    console.log('handleSubmit: ' + token)
    console.log('source:' + this.props.source)
    if (this.props.returnDate == '') {
      axios
        .get(
          `https://test.api.amadeus.com/v2/shopping/flight-offers?originLocationCode=${this.props.source}&destinationLocationCode=${this.props.destination}&departureDate=${this.props.departureDate}&adults=${this.props.adults}&children=${this.props.child}&infants=${this.props.infant}&travelClass=ECONOMY`,
          {
            headers: {
              Authorization: `Bearer ${token} `,
            },
          }
        )
        .then((response) => {
          //console.log(JSON.stringify(response.data))
          let flightinfo = {}
          console.log(response.data.dictionaries.aircraft)
          console.log(JSON.stringify(response.data.dictionaries.carriers))
          this.setState({
            flight: [
              {
                carrier: JSON.stringify(response.data.dictionaries.carriers),
                aircraft: JSON.stringify(response.data.dictionaries.aircraft),
              },
            ],
          })
          console.log('Flight loading' + this.state.flight)
        })
        .catch((error) => {
          console.log('error encountered: ' + error)
        })
    } else {
      axios
        .get(
          `https://test.api.amadeus.com/v2/shopping/flight-offers?originLocationCode=${this.props.source}&destinationLocationCode=${this.props.destination}&departureDate=${this.props.departureDate}&returnDate=${this.props.returnDate}&adults=1&travelClass=ECONOMY`,
          {
            headers: {
              Authorization: `Bearer ${token} `,
            },
          }
        )
        .then((response) => {
          console.log(JSON.stringify(response.data))
          console.log(response.data.dictionaries.aircraft.value)
        })
        .catch((error) => {
          console.log('error encountered: ' + error)
        })
      console.log(this.state.flights)
    }
    // axios
    //   .get(
    //     ,
    //     {
    //       headers: {
    //         Authorization: `Bearer ${token} `,
    //       },
    //     }
    //   )
    //   .then((response) => {
    //     console.log(JSON.stringify(response.data))
    //   })
    //   .catch((error) => {
    //     console.log('error encountered: ' + error)
    //   })
  }

  render() {
    return (
      <div>
        <button onClick={this.handleSubmit}>Submit Data</button>
        {this.state.flight.map((num) => (
          <li>
            {num.aircraft}
            {num.carrier}
          </li>
        ))}
      </div>
    )
  }
}

export default GetDataFromForm
