import React, { Component } from 'react'
import axios from 'axios'

let token = ''
export class GetDataFromForm extends Component {
  constructor(props) {
    super(props)

    this.state = {}
  }

  componentDidMount() {
    const axios = require('axios')
    var qs = require('qs')
    var data = qs.stringify({
      grant_type: 'client_credentials',
      client_id: 'egdfiMPA4upZ4DG3BXGuyPxqfFAetEVH',
      client_secret: 'bfPfw4TLGRkiF59F',
    })
    var config = {
      method: 'post',
      url: 'https://test.api.amadeus.com/v1/security/oauth2/token',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      data: data,
    }

    axios(config)
      .then((response) => {
        token = response.data.access_token
        console.log(JSON.stringify(response.data))
        console.log(token)
      })
      .catch((error) => {
        console.log(error)
      })
  }

  handleSubmit = (event) => {
    event.preventDefault()
    console.log(token)
    axios
      .get(
        `https://test.api.amadeus.com/v2/shopping/flight-offers?originLocationCode=${this.props.source}&destinationLocationCode=${this.props.destination}&departureDate=${this.props.departureDate}&adults=1&travelClass=ECONOMY`,
        {
          headers: {
            Authorization: `Bearer ${token} `,
          },
        }
      )
      .then((response) => {
        this.populateData(JSON.stringify(response.carriers))
        console.log(JSON.stringify(response.data))
        console.log('carriers' + JSON.stringify(this.state.flights))
      })
      .catch((error) => {
        console.log('error encountered: ' + error)
      })
  }

  render() {
    return (
      <div>
        <button onClick={this.handleSubmit}>Submit Data</button>
        <h1>{this.props.source}</h1>
      </div>
    )
  }
}

export default GetDataFromForm
