export class GetDataFromForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
      flight: [
        {},
        // {
        //   carrier: 'air France',
        //   aircraft: 'Boeing 777',
        //   depDate: 'dfsdss',
        //   arrDate: 'czczxcz',
        // },
      ],
      hello: [1, 2, 3, 4],
    }
  }

  componentDidMount() {
    var ref = fire.database().ref().child('FlightsData')
    // const querybase = require('querybase')
    // const query = querybase.ref(ref, ['source', 'destination'])
    // var queryComplete = query.where({
    //   source: 'vadodara',
    //   destination: 'delhi',
    // })
    // var count = -1
    ref.on('value', (snap) => {
      dup = snap.val()
      console.log(dup)
      this.setState({
        flight: snap.val(),
      })
    })
    console.log('object: ' + this.state.flight[0])

    // ref.once('value', (snap) => {
    //   // console.log(snap.val())
    //   // console.log('key: ' + snap.key)
    //   snap.forEach((dhaval) => {
    //     var data = dhaval.val()
