import React, { Component } from 'react'
import axios from 'axios'
import fire from './Firebase'

var dup = []
export class GetDataFromForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
      flight: [
        {},
        // {
        //   carrier: 'air France',
        //   aircraft: 'Boeing 777',
        //   depDate: 'dfsdss',
        //   arrDate: 'czczxcz',
        // },
      ],
      hello: [1, 2, 3, 4],
    }
  }

  componentDidMount() {
    const flightData = fire.database().ref('FlightsData')
    flightData
      .orderByChild('source')
      .startAt('vadodara')
      .on('value', (snapshot) => {
        console.log(dup[0])
        // dup =
        // snapshot.val()
        // console.log('dup array: ' + dup.map[0])
        // for (var i = 0; i < dup.length; i = i + 1) {
        //   if (
        //     dup[i].destination == 'delhi' &&
        //     dup[i].deptDate == '2021-03-10'
        //   ) {
        //     this.setState((prevState) => ({
        //       // flight: dup,
        //       flight: [...prevState.flight, dup],
        //     }))
        //   }
        // }
        this.setState({
          flight: snapshot.val(),
        })
        console.log('dfsdfsstate: ' + this.state.flight[0])
      })
  }

  handleSubmit = (event) => {
    event.preventDefault()
    let refernce = fire.database().ref('Search')
    const data = {
      source: this.props.source,
      destination: this.props.destination,
      departureDate: this.props.departureDate,
      string: 'dhaval agrawal',
    }

    refernce.push(data)
    // fire
    //   .database()
    //   .ref('search')
    //   .ref('destination')
    //   .push(this.props.destination)
    // fire.database().ref('search'). .push('hello dhaval')
  }

  render() {
    return (
      <div>
        <button onClick={this.handleSubmit}>Submit Data</button>
        {this.state.flight.map((num) => (
          <>
            <div>{num.source}</div>
            <div>{num.destination}</div>
            <div>{num.seats}</div>
            <div>{num.deptDate}</div>
          </>
        ))}
      </div>
    )
  }
}

export default GetDataFromForm
