import React, { Component } from 'react'
import fire from './Firebase'
import '../Components/Style.css'
import Login from './Login'

export class SignUp extends Component {
  constructor(props) {
    super(props)

    this.state = {
      name: '',
      email: '',
      password: '',
      confirmPassword: '',
      user: null,
    }
  }

  handleSignUp = (e) => {
    e.preventDefault()
    if (this.state.password !== this.state.confirmPassword) {
      // window.alert('password Do not Match')
      console.log('password Do not Match')
    } else {
      fire
        .auth()
        .createUserWithEmailAndPassword(this.state.email, this.state.password)
        .then((e) => {
          // window.alert("Sign Up Sucessfull" + e)
          console.log('Sign up Successful: ' + e)

          let refernce = fire.database().ref('users')
          const data = {
            name: this.state.name,
            email: this.props.destination,
          }
          refernce.push(data)

          this.setState({
            user: e,
          })
          console.log(e.email)
        })
        .catch((err) => {
          ;<p>Error: </p>
          // window.alert("Error: "+ err.toString())
          console.log('Error: ' + err.toString())
        })
    }
  }

  handleData = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    })
  }

  validate(event) {
    var pass = event.target.value
    var reg = /^(?=.[a-z])(?=.[A-Z])(?=.[0-9])(?=.[!@#\$%\^&\*])(?=.{6,})/
    var test = reg.test(pass)
    if (test) {
      console.log('pass')
    } else {
      console.log(
        'Please include at least 1 uppercase character, 1 lowercase character, 1 special character and 1 number.'
      )
    }
  }

  render() {
    return (
      <>
        <div class='main'>
          <div>
            <div class='signup'>
              <form action='#' class='signup_form'>
                <div class='img-overlay'></div>
                <h1>Join our platform to make your search easy</h1>
                <div class='inputs'>
                  <label for='name'>Name</label>
                  <input
                    id='name'
                    type='name'
                    placeholder='name'
                    name='name'
                    value={this.state.name}
                    onChange={this.handleData}
                  />
                </div>

                <div class='inputs'>
                  <label for='email'>Email</label>
                  <input
                    id='email'
                    type='email'
                    placeholder='email'
                    name='email'
                    value={this.state.email}
                    onChange={this.handleData}
                  />
                </div>

                <div class='inputs'>
                  <label for='password'>Password</label>
                  <input
                    id='password'
                    type='password'
                    placeholder='password'
                    name='password'
                    //   password="^(?=.[a-z])(?=.[A-Z])(?=.[0-9])(?=.[!@#\$%\^&\*])(?=.{8,})"

                    //  title="Please include at least 1 uppercase character, 1 lowercase character and 1 number."
                    value={this.state.password}
                    onChange={this.handleData}
                    onInput={this.validate}
                  />
                </div>

                <div class='inputs'>
                  <label for='password2'>Confirm Password</label>
                  <input
                    id='password2'
                    type='password'
                    placeholder='Confirm Password'
                    name='confirmPassword'
                    //   password="^(?=.[a-z])(?=.[A-Z])(?=.[0-9])(?=.[!@#\$%\^&\*])(?=.{8,})"

                    value={this.state.confirmPassword}
                    onChange={this.handleData}
                  />
                </div>

                <button class='btn' onClick={this.handleSignUp}>
                  Sign Up
                </button>
                <p id='question'>
                  Already a User ? <span>Login Tripr</span>
                </p>
              </form>
            </div>
          </div>
        </div>
      </>
    )
  }
}

export default SignUp
