import React, { Component } from 'react'

export class Form extends Component {
  constructor(props) {
    super()
    this.state = {
      source: '',
      destination: '',
      journeyType: '',
      adults: '',
      children: '',
      infants: '',
    }
  }

  handleSource = (event) => {
    this.setState({
      source: event.target.value,
    })
  }
  handleDestination = (event) => {
    this.setState({
      destination: event.target.value,
    })
  }

  handleChildren = (event) => {
    this.setState({
      children: event.target.value,
    })
  }

  handleAdults = (event) => {
    this.setState({
      adults: event.target.value,
    })
  }

  handleInfants = (event) => {
    this.setState({
      infants: event.target.value,
    })
  }

  handleJourney = (event) => {
    this.setState({
      journeyType: event.target.value,
    })
  }

  render() {
    return (
      <form>
        <label>
          journey Type:
          <select value={this.state.journeyType} onChange={this.handleJourney}>
            console.log(this.set.journeyType);
            <option selected value='One Way'>
              One Way
            </option>
            <option value='Round Trip'>Round Trip</option>
            <option value='multicity'>Multi City</option>
          </select>
        </label>

        <div>
          <input
            type='text'
            placeholder='Source'
            name='source'
            value={this.state.source}
            onChange={this.handleSource}
          />
        </div>
        <div>
          <input
            type='text'
            placeholder='Destination'
            name='destination'
            value={this.state.destination}
            onChange={this.handleDestination}
          />
        </div>
        <div>
          <input type='date' placeholder='select Date' name='date' />
        </div>
        <div>
          <input
            type='number'
            placeholder='No. of Adults'
            name='adults'
            value={this.state.adults}
            onChange={this.handleAdults}
          />
        </div>
        <div>
          <input
            type='number'
            placeholder='No. of Children'
            name='children'
            value={this.state.children}
            onChange={this.handleChildren}
          />
        </div>
        <div>
          <input
            type='number'
            placeholder='No. of Infants'
            name='infants'
            value={this.state.infants}
            onChange={this.handleInfants}
          />
        </div>
        <div>
          <button>Submit</button>
        </div>
      </form>
    )
  }
}

export default Form
