import React, { Component } from 'react'
import axios from 'axios'
import fire from './Firebase'
;<script src='https://unpkg.com/querybase@0.6.0'></script>

var dup = [{}]
var temp = [{}]
export class GetDataFromForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
      flight: [
        {},
        // {
        //   carrier: 'air France',
        //   aircraft: 'Boeing 777',
        //   depDate: 'dfsdss',
        //   arrDate: 'czczxcz',
        // },
      ],
      //hello: [1, 2, 3, 4],
    }
  }

  // fetchValue = (val) => {
  //   for (var i = 0; i < val.length; i++) {
  //     if (val[i].source == 'vadodara' && val[i] == 'bombay') {
  //       this.setState({
  //         flight: [...this.state.flight, dup[i]],
  //       })
  //     }
  //   }
  // }

  componentDidMount() {
    var ref = fire.database().ref().child('FlightsData')
    ref.on('value', (snap) => {
      dup = snap.val()
      console.log(dup)
      // this.setState({
      //   flight: snap.val(),
      // })
      //this.fetchValue(dup)
    })
    console.log('object: ' + this.state.flight[0])

    console.log('temp : ' + this.state.flight[0])
    for (var i = 0; i < dup.length; i++) {
      if (
        dup[i].source == 'vadodara' &&
        dup[i].destination == 'delhi' &&
        dup[i].deptDate == '2021-03-10'
      ) {
        console.log('baroda')
        // this.setState({
        //   flight: [...this.state.flight, dup[i]],
        // })
        temp[i] = dup[i]
      } else {
        console.log('not working')
      }
    }

    this.setState({
      flight: temp,
    })

    console.log('value of flight: ' + this.state.flight)
  }

  handleSubmit = (event) => {
    event.preventDefault()
    // let refernce = fire.database().ref('Search')
    // const data = {
    //   source: this.props.source,
    //   destination: this.props.destination,
    //   departureDate: this.props.departureDate,
    //   string: 'dhaval agrawal',
    // }

    // refernce.push(data)
    // fire
    //   .database()
    //   .ref('search')
    //   .ref('destination')
    //   .push(this.props.destination)
    // fire.database().ref('search'). .push('hello dhaval')
  }

  render() {
    return (
      <div>
        <button onClick={this.handleSubmit}>Submit Data</button>
        {/* //if dup.destination == 'delhi' */}
        {/* {this.state.flight.map((num) => (
          <>
            <div>{num.source}</div>
            <div>{num.destination}</div>
            <div>{num.seats}</div>
            <div>{num.deptDate}</div>
          </>
        ))} */}
        {Object.entries(this.state.flight).map(([key, value]) => (
          <div key={key}>
            <div>{value.source}</div>
            <div>{value.destination}</div>
            <div>{value.seats}</div>
            <div>{value.deptDate}</div>
            {/* {console.log('hello' + value)}; */}
          </div>
        ))}
      </div>
    )
  }
}

export default GetDataFromForm
