    console.log('handleInfo method called')
    // this.setState({
    //   flightInfo: value,
    // })
    this.forceUpdate()
    return <h1>dhaval agrawal</h1>
  }

  render() {
    return (
      <div>
        <button onClick={() => this.handleSubmit}>Submit Data</button>
        {Object.entries(this.state.flight).map(([key, value]) => (
          <div key={key}>
            <div>{value.source}</div>
            <div>{value.destination}</div>
            <div>{value.deptDate}</div>
            <button
              hidden={value.seats == null ? true : false}
              onClick={(e) => {
                e.preventDefault()
                this.handleInfo(value)
              }}
            >
              More info
            </button>
          </div>
        ))}
      </div>
    )
  }
}

export default GetDataFromForm
