var dup = [{}]
var temp = [{}]
export class GetDataFromForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
      flight: [
        {},
        // {
        //   carrier: 'air France',
        //   aircraft: 'Boeing 777',
        //   depDate: 'dfsdss',
        //   arrDate: 'czczxcz',
        // },
      ],
      hello: [1, 2, 3, 4],
    }
  }

  componentDidMount() {
    var ref = fire.database().ref().child('FlightsData')
    const querybase = require('querybase')
    const query = querybase.ref(ref, ['source'])
    const queryComplete = query.where({
      source: 'vadodara',
    })
    // var count = -1
    ref.on('value', (snap) => {
      dup = snap.val()
      console.log(dup)
    })

    // ref.once('value', (snap) => {
    //   // console.log(snap.val())
    //   // console.log('key: ' + snap.key)
    //   snap.forEach((dhaval) => {
    //     var data = dhaval.val()
    //     console.log('data: ' + data)
    //   })
    // })
    for (var i = 0; i < dup.length; i = i + 1) {
      console.log('tata')
      if (dup[i]['source'] == 'vadodara') {
        // this.setState({
        //   flight: dup,
        // })
        // alert(dup[i])
        temp[i] = dup[i]
      } else {
        // alert('tirth : ' + i)
      }
    }
    console.log('temp : ' + temp)
  }

  handleSubmit = (event) => {
    event.preventDefault()
    let refernce = fire.database().ref('Search')
    const data = {
      source: this.props.source,
      destination: this.props.destination,
      departureDate: this.props.departureDate,
      string: 'dhaval agrawal',
    }

    refernce.push(data)
    // fire
    //   .database()
    //   .ref('search')
    //   .ref('destination')
    //   .push(this.props.destination)
    // fire.database().ref('search'). .push('hello dhaval')
  }

  render() {
    return (
      <div>
        <button onClick={this.handleSubmit}>Submit Data</button>
        {temp.map((num) => (
          <>
            <div>{num.source}</div>
            <div>{num.destination}</div>
            <div>{num.seats}</div>
            <div>{num.deptDate}</div>
          </>
        ))}
      </div>
    )
  }
}

export default GetDataFromForm
