import React, { Component } from 'react'
import fire from './Components/Firebase'

export class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      user: null,
    }
  }

  componentDidMount() {}

  authListener = () => {
    fire.auth().onAuthStateChanged((user) => {
      if (user) {
        this.setState({ user })
      } else {
        this.setState({ user: null })
      }
    })
  }

  render() {
    return (
      <div className='App'>
        <h1>hello Dhaval</h1>
        {this.state.user ? <Form></Form> : <Login></Login>}

        {/* <Product></Product> */}
        {/* <LifeCycle></LifeCycle> */}
      </div>
    )
  }
}

export default App
