import React, { Component } from 'react'
import fire from './Firebase'
import FunComp from './FunComp'
import TempD from './TempD'

var dup = [{}]
var temp = [{}]
export class GetDataFromForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
      flight: [
        {},
        // {
        //   carrier: 'air France',
