    console.log('ComponentDidMount method called')
  }

  handleSubmit = (e) => {
    e.preventDefault()

    var ref = fire.database().ref().child('FlightsData')
    ref.on('value', (snap) => {
      dup = snap.val()
      console.log(dup)
    })

    for (var i = 0; i < dup.length; i++) {
      if (
        dup[i].source == this.props.source &&
        dup[i].destination == this.props.destination &&
        dup[i].deptDate == this.props.departureDate
      ) {
        console.log('baroda')
        temp[i] = dup[i]
      } else {
        console.log('not working')
      }
    }

    this.setState({
      flight: temp,
    })
    console.log('fsfsdfsdzgsdgd')

    // this.fetchData()

    // let refernce = fire.database().ref('Search')
