import React, { Component } from 'react'
import axios from 'axios'
import fire from './Firebase'

let token = ''
var dup = [
  {
    carry: '',
    airc: '',
  },
]
export class GetDataFromForm extends Component {
  constructor(props) {
    super(props)

    this.state = {
      flight: [
        {},
        // {
        //   carrier: 'air France',
        //   aircraft: 'Boeing 777',
        //   depDate: 'dfsdss',
        //   arrDate: 'czczxcz',
        // },
      ],
      hello: [1, 2, 3, 4],
    }
  }

  componentDidMount() {
    const flightData = fire.database().ref('FlightsData')
    if (
      flightData.child('source').isEqual('vadodara') &&
      flightData.child('destination').isEqual('delhi')
    ) {
      console.log('flight exists')
    }
    // flightData.on('value', (snapshot) => {
    //   console.log(snapshot.val())
    //   this.setState({
    //     flight: snapshot.val(),
    //   })
    // })
  }

  handleSubmit = (event) => {
    event.preventDefault()
    let refernce = fire.database().ref('Search')
    const data = {
      source: this.props.source,
      destination: this.props.destination,
      departureDate: this.props.departureDate,
      string: 'dhaval agrawal',
    }

    refernce.push(data)
    // fire
    //   .database()
    //   .ref('search')
    //   .ref('destination')
    //   .push(this.props.destination)
    // fire.database().ref('search'). .push('hello dhaval')
  }

  render() {
    return (
      <div>
        <button onClick={this.handleSubmit}>Submit Data</button>
        {this.state.flight.map((num) => (
          <>
            <div>{num.source}</div>
            <div>{num.destination}</div>
            <div>{num.seats}</div>
            <div>{num.deptDate}</div>
          </>
        ))}
      </div>
    )
  }
}

export default GetDataFromForm
