import React, { Component } from 'react'

export default class Product extends Component {
  constructor(props) {
    super(props)

    this.state = {
      ProductId: '',
      qty: 0,
    }
  }

  addtoCart = (pid) => {
    console.log(pid)
    this.setState({
      ProductId: pid,
      qty: this.state.qty + 1,
    })
  }

  render() {
    return (
      <div>
        <button onClick={() => this.addtoCart(2)}>Add to Cart</button>
        <Cart qty={this.state.qty}></Cart>
      </div>
    )
  }
}

