import React, { Component } from 'react'
import axios from 'axios'

export class Form extends Component {
  constructor(props) {
    super()
    this.state = {
      source: '',
      destination: '',
      date: '',
      journeyType: '',
      adults: '',
      children: '',
      infants: '',
    }
  }

  componentDidMount() {
    const axios = require('axios')
    var qs = require('qs')
    var data = qs.stringify({
      grant_type: 'client_credentials',
      client_id: 'egdfiMPA4upZ4DG3BXGuyPxqfFAetEVH',
      client_secret: 'bfPfw4TLGRkiF59F',
    })
    var config = {
      method: 'post',
      url: 'https://test.api.amadeus.com/v1/security/oauth2/token',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      data: data,
    }

    let tempToken = ''
    axios(config)
      .then((response) => {
        tempToken = response.data.access_token
        console.log(JSON.stringify(response.data))
        console.log(tempToken)
      })
      .catch((error) => {
        console.log(error)
      })
  }

  handleSource = (event) => {
    this.setState({
      source: event.target.value,
    })

    const axios = require('axios')
    var FormData = require('form-data')
    var data = new FormData()

    var config = {
      method: 'get',
      url:
        'https://test.api.amadeus.com/v1/reference-data/locations?subType=AIRPORT&keyword=event.target.value',
      headers: {
        Authorization: 'Bearer RqzBLL43UjZAzIuWG8x1ENZyZGFK',
        // ...data.getHeaders(),
      },
      data: data,
    }

    axios(config)
      .then(function (response) {
        console.log(JSON.stringify(response.data))
      })
      .catch(function (error) {
        console.log(error)
      })
  }
  handleDestination = (event) => {
    this.setState({
      destination: event.target.value,
    })
  }

  handleChildren = (event) => {
    this.setState({
      children: event.target.value,
    })
  }

  handleDate = (event) => {
    this.setState({
      date: event.target.value,
    })
  }

  handleAdults = (event) => {
    this.setState({
      adults: event.target.value,
    })
  }

  handleInfants = (event) => {
    this.setState({
      infants: event.target.value,
    })
  }

  tryDate() {
    if (this.state.journeyType == 'Round Trip') {
      return <input type='date' />
    }
  }

  handleJourney = (event) => {
    this.setState({
      journeyType: event.target.value,
    })
  }

  handleSubmit = (event) => {
    event.preventDefault()

    var axios = require('axios')
    var data = JSON.stringify({
      currencyCode: 'USD',
      originDestinations: [
        {
          id: '1',
          originLocationCode: this.state.source,
          destinationLocationCode: this.state.destination,
          departureDateTimeRange: { date: '2021-04-02', time: '10:00:00' },
        },
      ],
      travelers: [
        { id: '1', travelerType: 'ADULT' },
        {
          id: '2',
          travelerType: 'CHILD',
        },
      ],
      sources: ['GDS'],
    })

    var config = {
      method: 'post',
      url: 'https://test.api.amadeus.com/v2//shopping/flight-offers',
      headers: {
        // 'X-HTTP-Method-Override': 'GET',
        Authorization: 'Bearer {tempToken}',
        'Content-Type': 'application/json',
      },
      data: data,
    }

    axios(config)
      .then(function (response) {
        console.log(JSON.stringify(response.data))
      })
      .catch(function (error) {
        console.log(error)
      })

    console.log(
      `${this.state.journeyType} ${this.state.destination} ${this.state.source}`
    )
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          journey Type:
          <select value={this.state.journeyType} onChange={this.handleJourney}>
            <option selected value=''></option>
            <option value='One Way'>One Way</option>
            <option value='Round Trip'>Round Trip</option>
            <option value='multicity'>Multi City</option>
          </select>
        </label>
        <div>
          <input
            type='text'
            placeholder='Source'
            name='source'
            value={this.state.source}
            onChange={this.handleSource}
          />
        </div>
        <div>
          <input
            type='text'
            placeholder='Destination'
            name='destination'
            value={this.state.destination}
            onChange={this.handleDestination}
          />
        </div>
        <div>{this.tryDate()}</div>
        <div>
          <input
            type='date'
            placeholder='select Date'
            name='date'
            value={this.state.date}
            onChange={this.handleDate}
          />
        </div>

        <div>
          <input
            type='number'
            placeholder='No. of Adults'
            name='adults'
            value={this.state.adults}
            onChange={this.handleAdults}
          />
        </div>
        <div>
          <input
            type='number'
            placeholder='No. of Children'
            name='children'
            value={this.state.children}
            onChange={this.handleChildren}
          />
        </div>
        <div>
          <input
            type='number'
            placeholder='No. of Infants'
            name='infants'
            value={this.state.infants}
            onChange={this.handleInfants}
          />
        </div>
        <div>
          <button>Submit</button>
        </div>
      </form>
    )
  }
}

export default Form
